import React, { useState } from 'react'; 
import content from './images/content.jpg';
import style from './images/style.jpg';
import * as mi from '@magenta/image';

function StyleTransfer(){
    const model = new mi.ArbitraryStyleTransferNetwork();
    const contentImg = document.getElementById('content');
    const styleImg = document.getElementById('style');
    const canvas = document.getElementById('stylized');


    // console.log(model);
    // console.log(stylizedCanvas);

    function stylization () {
        model.initialize().then(() => {
            stylize();})}

    async function stylize() {
        // await clearCanvas();
        const ctx = canvas.getContext('2d');
        // Resize the canvas to be the same size as the source image.
        canvas.width = contentImg.width;
        canvas.height = contentImg.height;

        // This does all the work!
        model.stylize(contentImg, styleImg).then((imageData) => {
            ctx.putImageData(imageData, 0, 0);
        });
        }

    return (
        <div>
            <img id = 'style'  height="256" src = {style} ></img>
            <img id = 'content'  height="256" src = {content}  height = "500" width = "500"></img>
            <canvas id = 'stylized' width="192" height="256">
                <rect width="300" height="100"></rect>
            </canvas>
            <button onClick ={stylization}>Transform!</button>
        </div>
    )
}

export default StyleTransfer;
