    useEffect(() => { setavatarDetails(props.avaDetails) }, [props.avaDetails]);


    function setAva(){
        const newDetails = {...avatarDetails, 'skinColor':'Black'};
        setavatarDetails(newDetails);
    }

    return (
        <div>
            <div>
                <button onClick = {setAva}>
                    stuff
                </button>
            </div>
               <p> {avatarDetails['skinColor']} </p>
        </div>
    );

}


export default ModAvatar;
